import React from 'react';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import { TextareaAutosize } from '@material-ui/core';
import { uuid } from 'uuidv4';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
      width: '25ch',
    },
  },
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: 200,
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));
export default class FormPropsTextFields {
  classes = useStyles();

  IR_Number = () => {
    let year = new Date().getFullYear();
    // console.log(year.getFullYear());
    let id = uuid(1);
    return `${year}-${id}`;
  };
  person_involved = {
    last_name: 'z',
    first_name: 'z',
    middle_name: 'z',
    relevance: ''
  };

  state = {
    IR_Number: IR_Number,
    time_of_incident: '',
    incident_type: '',
    location: '',
    // people_involed: [person_involved],
    // narrative: '',
    // signature: {
    //   reporting_officer: '',
    //   badge_number: '',
    //   time_of_report: ''
    // }
  };
  render() {
    return (
      <form className={classes.root} noValidate autoComplete="off">
        <h1>Report an Incident</h1>
        <div>
          <TextField id="standard-number" value={IR_Number()} label="IR Number" type="string" InputLabelProps={{ shrink: true, }} />
          <TextField id="datetime-local" label="Time of Incident" type="datetime-local" placeholder="YYYY-MM-DDT" className={classes.textField} InputLabelProps={{ shrink: true, }} />
          <TextField required id="standard-required" label="Incident Type" placeholder="" />
          <TextField required id="standard-required" label="Location" placeholder="" />
        </div>
        <h2>Suspects</h2>
        <div>
          <TextField required id="standard-required" label="Last Name" value={this.state.people_involed.last_name} placeholder="Doe" />
          <TextField required id="standard-required" label="First Name" placeholder="John" />
          <TextField required id="standard-required" label="Middle Name or Initial" placeholder="A." />
          <FormControl className={classes.formControl}>
            <InputLabel id="demo-simple-select-label">Relevance</InputLabel>
            <Select
              labelId="demo-simple-select-label"
              id="demo-simple-select"
              value={relevance}
              onChange={handleChange}
            >
              <MenuItem value={"RP"}>RP</MenuItem>
              <MenuItem value={"W"}>W</MenuItem>
              <MenuItem value={"V"}>V</MenuItem>
              <MenuItem value={"IL"}>IL</MenuItem>
              <MenuItem value={"S"}>S</MenuItem>
            </Select>
          </FormControl>
        </div>
        <h2>Narrative</h2>
        <div>
          <TextareaAutosize rowsMin={10}
            id="filled-full-width"
            label="Narrative"
            style={{
              margin: 8,
              width: 1000
            }}
            placeholder=""
            helperText=""
            fullWidth
            margin="normal"
            InputLabelProps={{
              shrink: true,
            }}
            variant="filled"
          />
        </div>
        <h2>Signature</h2>
        <div>
          <TextField required id="standard-required" label="Reporting Officer" placeholder="" />
          <TextField
            id="standard-number"
            label="Badge Number"
            type="number"
            InputLabelProps={{
              shrink: true,
            }}
          />
          <TextField
            id="datetime-local"
            label="Time of Filed Report"
            type="datetime-local"
            placeholder="YYYY-MM-DDT"
            className={classes.textField}
            InputLabelProps={{
              shrink: true,
            }}
          />
        </div>
      </form>
    );
  }
}
