import PeopleGrid from './Components/people_grid';
import PersonCard from './Components/person';
import NavigationBar from './Components/navbar';
// Router
import { Route, BrowserRouter as Router, Switch } from 'react-router-dom';
// Firebase
import firebase from 'firebase';
import { useState, useEffect } from 'react';
import { app } from './Components/firebase';

function App() {
  const AuthPage = () => {
    const [currentUser, setCurrentUser] = useState();
    const provider = new firebase.auth.GoogleAuthProvider();
    const authWithGoogle = () => {
      firebase.auth().signInWithPopup(provider);
    };

    useEffect(() => {
      app.auth().onAuthStateChanged((user) => {
        setCurrentUser(user);
      });
    }, []);

    return (<div><p>{currentUser.displayName}</p><button onClick={authWithGoogle}>Login Here</button></div>);
  };


  return (
    <div>
      <Router>
        <AuthPage />
        <NavigationBar />
        <Switch>
          <Route path='/people' component={PersonCard} />
          {/* <Route path='/incidents' component={ } /> */}
          <Route path='/home' exact component={PeopleGrid} />
          <Route path='/' exact component={PeopleGrid} />
        </Switch>
      </Router>
    </div>
  );
}

export default App;
