//  ================= Get People ==============
import firebase from 'firebase';
import { Fragment, useState, useEffect } from 'react';
//  ================= Get People ==============
// ================== Card Imports ============
import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Button from '@material-ui/core/Button';
// ================== Card Imports ============
// ============== Accordion Imports ===========
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
import Typography from '@material-ui/core/Typography';
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
// ============== Accordion Imports ===========

const cardStyles = makeStyles({
   root: {
      minWidth: 275,
      maxWidth: '25vw'
   },
});

const useStyles = makeStyles((theme) => ({
   root: {
      width: '100%',
   },
   heading: {
      fontSize: theme.typography.pxToRem(15),
      fontWeight: theme.typography.fontWeightRegular,
   },
}));


export default function Test() {
   const classes = cardStyles();
   const accordionClasses = useStyles();
   // Has to load whole People collection to get random person... for now...
   const [people, setPeople] = useState([]);
   const [loading, setLoading] = useState(true);
   const ref = firebase.firestore().collection("People");
   var person;
   async function getPeople() {
      setLoading(true);
      ref.onSnapshot((querySnapshot) => {
         const items = [];
         querySnapshot.forEach((person) => {
            items.push(person.data());
         });
         setPeople(items);
         setLoading(false);
         // console.log(people);
      });
   }

   useEffect(() => {
      getPeople();
   }, []);

   if (loading) {
      return (<h1>Loading...</h1>);
   } else {
      person = people[Math.floor(Math.random() * people.length)];
   }
   console.log(person);
   // Has to load whole People collection to get random person... for now...
   // Card content and integration
   const bull = <span className={classes.bullet}>•</span>;

   return (<Fragment><Card className={classes.root}>
      <CardContent>
         <h1>{`${person.first_name} ${person.last_name}`}</h1>
         <Accordion>
            <AccordionSummary
               expandIcon={<ExpandMoreIcon />}
               aria-controls="panel1a-content"
               id="panel1a-header"
            >
               <Typography className={classes.heading}>Demographic Information</Typography>
            </AccordionSummary>
            <AccordionDetails>
               <table>
                  <tbody>
                     <tr>
                        <th>Age</th>
                        <td>{person.demographic.age}</td>
                     </tr>
                  </tbody>
               </table>
            </AccordionDetails>
         </Accordion>
      </CardContent>
      <CardActions>
         <Button size="small">Learn More</Button>
      </CardActions>
   </Card></Fragment>);
   // Card content and integration
}
