// ========================= Appbar ==============================
import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import InputBase from '@material-ui/core/InputBase';
import { fade, makeStyles } from '@material-ui/core/styles';
import MenuIcon from '@material-ui/icons/Menu';
import SearchIcon from '@material-ui/icons/Search';
import { Menu } from '@material-ui/core';
// ========================= Appbar ==============================
// ========================= Accordion ============================
import Accordion from '@material-ui/core/Accordion';
import AccordionSummary from '@material-ui/core/AccordionSummary';
import AccordionDetails from '@material-ui/core/AccordionDetails';
// ========================= Accordion ============================

const appBarStyles = makeStyles((theme) => ({
   root: {
      flexGrow: 1,
   },
   menuButton: {
      marginRight: theme.spacing(2),
      background: 'red'
   },
   title: {
      flexGrow: 1,
      display: 'none',
      [theme.breakpoints.up('sm')]: {
         display: 'block',
      },
   },
   search: {
      position: 'relative',
      borderRadius: theme.shape.borderRadius,
      backgroundColor: fade(theme.palette.common.white, 0.15),
      '&:hover': {
         backgroundColor: fade(theme.palette.common.white, 0.25),
      },
      marginLeft: 0,
      width: '100%',
      [theme.breakpoints.up('sm')]: {
         marginLeft: theme.spacing(1),
         width: 'auto',
      },
   },
   searchIcon: {
      padding: theme.spacing(0, 2),
      height: '100%',
      position: 'absolute',
      pointerEvents: 'none',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
   },
   inputRoot: {
      color: 'inherit',
   },
   inputInput: {
      padding: theme.spacing(1, 1, 1, 0),
      // vertical padding + font size from searchIcon
      paddingLeft: `calc(1em + ${theme.spacing(4)}px)`,
      transition: theme.transitions.create('width'),
      width: '100%',
      [theme.breakpoints.up('sm')]: {
         width: '12ch',
         '&:focus': {
            width: '20ch',
         },
      },
   },
}));

const useStyles = makeStyles((theme) => ({
   heading: {
      // background: 'rgb(100, 100, 100)'
   },
}));

export default function SearchAppBar() {
   const app_bar_classes = appBarStyles();
   const classes = useStyles();

   return (
      <div className={app_bar_classes.root}>
         <AppBar position="static">
            <Toolbar>
               <IconButton
                  edge="start"
                  className={app_bar_classes.menuButton}
                  color="inherit"
                  aria-label="open drawer"
               >
                  {/* ============================ Accordion ============================ */}
                  <Accordion>
                     <AccordionSummary
                     >
                        <Typography className={classes.heading}><MenuIcon /></Typography>
                     </AccordionSummary>
                     <AccordionDetails>
                     </AccordionDetails>
                  </Accordion>
                  {/* ============================ Accordion ============================ */}
               </IconButton>
               <Typography className={app_bar_classes.title} variant="h6" noWrap>
                  Material-UI
            </Typography>
               <div className={app_bar_classes.search}>
                  <div className={app_bar_classes.searchIcon}>
                     <SearchIcon />
                  </div>
                  <InputBase
                     placeholder="Searchâ€¦"
                     classes={{
                        root: app_bar_classes.inputRoot,
                        input: app_bar_classes.inputInput,
                     }}
                     inputProps={{ 'aria-label': 'search' }}
                  />
               </div>
            </Toolbar>
         </AppBar>
      </div>
   );
}
