import React from 'react';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import TextareaAutosize from '@material-ui/core/TextareaAutosize';
// Zack
import { useForm, Controller } from 'react-hook-form';
import uuid from 'uuidv4';
import firebase from 'firebase';
// Zack

const useStyles = makeStyles((theme) => ({
    root: {
        width: '100vw',
        margin: 'auto',
        // margin: '2em auto',
        '& .MuiTextField-root': {
            margin: theme.spacing(1),
            width: '25ch',
        },
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
    selectEmpty: {
        marginTop: theme.spacing(2),
    },
    container: {
        display: 'flex',
        flexWrap: 'wrap',
    },
    textField: {
        marginLeft: theme.spacing(1),
        marginRight: theme.spacing(1),
        width: 200,
    },

}));

export default function FormPropsTextFields() {
    const classes = useStyles();
    // ========================= Zack's Stuff =========================
    const { register, handleSubmit } = useForm();
    let submittedForm;
    var personObject = {
        // id: uuid(),
        report_code: '',
        last_name: '',
        first_name: '',
        middle_name: '',
        ssn: '',
        driver_license: {
            number: '',
            state: '',
            expiration: ''
        },
        hazard: '',
        demographic: {
            race: '',
            sex: '',
            height: '',
            weight: '',
            eye_color: '',
            hair_color: '',
            date_of_birth: '',
            age: '',
            special_features: {
                face: '',
                body: '',
                arms: ''
            }
        },
        address: '',
        phone_number: '',
        known_incidents: [],
        vehicles: [],
        gang_affiliated: '',
        mugshots: ''
    };

    const onSubmit = (data) => {
        submittedForm = data;
        console.log(`The form submitted was: `, submittedForm);
        firebase.firestore().collection('People').doc(submittedForm.uuid).set(submittedForm);
    };
    // ========================= Zack's Stuff =========================
    return (
        <form className={classes.root} onSubmit={handleSubmit(onSubmit)} noValidate autoComplete="off">
            <h1>File a Person's Report</h1>
            <h2>Demographics</h2>
            <TextField inputRef={register} required label="Last Name" defaultValue="" />
            <TextField inputRef={register} required label="First Name" defaultValue="" />
            <TextField inputRef={register} required label="Middle Name or Initial" defaultValue="" />
            <br />
            {/* placeholder until figure out what to do for license */}
            <TextField inputRef={register} name="number" required label="License" defaultValue="" placeholder="AZ#######" />
            <FormControl className={classes.formControl}>
                <InputLabel id="demo-simple-select-label">State</InputLabel>
                <Select
                    inputRef={register}
                    name="state"
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                >
                    <MenuItem value='AZ'>AZ</MenuItem>
                    <MenuItem value='CA'>CA</MenuItem>
                    <MenuItem value='TX'>TX</MenuItem>
                </Select>
            </FormControl>
            <TextField
                inputRef={register}
                name="expiration"
                id="expDate"
                label="Exp. Date"
                type="date"
                defaultValue="mm-dd-yyyy"
                className={classes.textField}
                InputLabelProps={{
                    shrink: true,
                }}
            />
            <TextField
                inputRef={register}
                name="ssn"
                label="SSN"
                type="number"
            />
            <br />
            <TextField inputRef={register} required label="Race" defaultValue="" />
            <FormControl className={classes.formControl}>
                <InputLabel id="demo-simple-select-label">Gender</InputLabel>
                <Controller as={
                    <Select
                        labelId="demo-simple-select-label"
                        id="demo-simple-select"
                    >
                        <MenuItem value={'F'}>Female</MenuItem>
                        <MenuItem value={'M'}>Male</MenuItem>
                        <MenuItem value={'U'}>Unknown</MenuItem>
                    </Select>}
                    control={control}
                />
            </FormControl>
            <TextField
                inputRef={register}

                label="Height"
                type="number"
                placeholder="###"
            />
            <TextField
                inputRef={register}

                label="Weight"
                type="number"
                placeholder="###"
            />
            <br />
            <FormControl className={classes.formControl}>
                <InputLabel id="demo-simple-select-label">Eye Color</InputLabel>
                <Select
                    inputRef={register}
                    name="eye_color"
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                >
                    <MenuItem value={'Brown'}>Brown</MenuItem>
                    <MenuItem value={'Black'}>Black</MenuItem>
                    <MenuItem value={'Blue'}>Blue</MenuItem>
                </Select>
            </FormControl>
            <FormControl className={classes.formControl}>
                <InputLabel id="demo-simple-select-label">Hair Color</InputLabel>
                <Select
                    name="hair_color"
                    labelId="demo-simple-select-label"
                    id="demo-simple-select"
                >
                    <MenuItem value={'Brown'}>Brown</MenuItem>
                    <MenuItem value={'Black'}>Black</MenuItem>
                    <MenuItem value={'Blonde'}>Blonde</MenuItem>
                </Select>
            </FormControl>
            <TextField inputRef={register} name="date_of_birth" id="DateofBirth" label="Date of Birth" type="date" defaultValue="mm-dd-yyyy" className={classes.textField} InputLabelProps={{ shrink: true, }} />
            {/* <TextField inputRef={register} name="age" label="Age" type="number" InputLabelProps={{ shrink: true, }} /> */}
            {/* <br /> */}
            <h3>Features</h3>
            <TextareaAutosize

                rowsMin={5}
                id="filled-full-width"
                label="Narrative"
                name="features"
                placeholder="Tattoos, scars, noticeable features"
                style={{
                    margin: 8,
                    width: 200
                }}
                helperText=""
                fullWidth
                margin="normal"
                InputLabelProps={{
                    shrink: true,
                }}
                variant="filled"
            />
            <input type="submit" />
        </form>
    );
}
